import React, { useState } from "react";
import MapView from "./components/MapView";
import { Pin } from "./types";

const initialPins: Pin[] = [];

function App() {
  const [pins, setPins] = useState<Pin[]>(initialPins);
  const [selectedPins, setSelectedPins] = useState<number[]>([]);
  const [showTravelPopup, setShowTravelPopup] = useState(false);
  const [travelTrace, setTravelTrace] = useState<[number, number][] | null>(
    null,
  );

  // Handle map click to add a new pin
  const handleMapClick = (e: any) => {
    if (!e.latlng) return;
    const coords: [number, number] = [e.latlng.lat, e.latlng.lng];
    const placeName = prompt("Enter the name of this place:");
    if (!placeName) return;
    setPins((prev) => [...prev, { coords, placeName, media: [] }]);
  };

  // Handle media upload for a pin
  const handleMediaUpload = (pinIdx: number, files: FileList) => {
    const fileObjs = Array.from(files).map((file) => ({
      url: URL.createObjectURL(file),
      type: file.type,
      name: file.name,
    }));
    setPins((prev) =>
      prev.map((pin, idx) =>
        idx === pinIdx ? { ...pin, media: [...pin.media, ...fileObjs] } : pin,
      ),
    );
  };

  // Handle pin selection (by index)
  const handlePinSelect = (pinIdx: number) => {
    if (selectedPins.includes(pinIdx)) {
      setSelectedPins(selectedPins.filter((idx) => idx !== pinIdx));
    } else if (selectedPins.length < 2) {
      setSelectedPins([...selectedPins, pinIdx]);
    } else if (selectedPins.length === 2) {
      // Replace the first selected with the new one
      setSelectedPins([selectedPins[1], pinIdx]);
    }
  };

  // Show popup when exactly 2 pins are selected
  React.useEffect(() => {
    if (selectedPins.length === 2) {
      setShowTravelPopup(true);
    } else {
      setShowTravelPopup(false);
      setTravelTrace(null);
    }
  }, [selectedPins]);

  // Handle "TRAVEL" button click
  const handleTravel = () => {
    if (selectedPins.length === 2) {
      const coords1 = pins[selectedPins[0]].coords;
      const coords2 = pins[selectedPins[1]].coords;
      setTravelTrace([coords1, coords2]);
      setShowTravelPopup(false);
    }
  };

  // Deselect pins when clicking outside popup
  const handleClosePopup = () => {
    setShowTravelPopup(false);
    setSelectedPins([]);
  };

  return (
    <div className="h-screen w-screen bg-gray-100 relative">
      <MapView
        pins={pins}
        onMapClick={handleMapClick}
        onMediaUpload={handleMediaUpload}
        onPinSelect={handlePinSelect}
        selectedPins={selectedPins}
        travelTrace={travelTrace}
      />
      {showTravelPopup && (
        <div className="absolute top-8 left-1/2 transform -translate-x-1/2 z-[1000] bg-white border border-gray-300 rounded shadow-lg p-6 flex flex-col items-center">
          <div className="mb-4 text-lg font-semibold">Travel between:</div>
          <div className="mb-2">
            <span className="font-bold">
              {pins[selectedPins[0]]?.placeName}
            </span>{" "}
            and{" "}
            <span className="font-bold">
              {pins[selectedPins[1]]?.placeName}
            </span>
          </div>
          <button
            className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 font-bold mb-2"
            onClick={handleTravel}
          >
            TRAVEL
          </button>
          <button
            className="text-xs text-gray-500 underline"
            onClick={handleClosePopup}
          >
            Cancel
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
