import React, { useState } from "react";
import MapView from "./components/MapView";
import { Pin, Travel } from "./types";

const initialPins: Pin[] = [];

// Load OpenRouteService API key from environment variable
const ORS_API_KEY =
  import.meta.env?.ORS_API_KEY || import.meta.env?.VITE_ORS_API_KEY;

async function fetchORSRoute(
  start: [number, number],
  end: [number, number],
): Promise<[number, number][]> {
  // const url =
  //   "https://api.openrouteservice.org/v2/directions/driving-car/geojson";
  const url =
    "https://corsproxy.io/?" +
    encodeURIComponent(
      "https://api.openrouteservice.org/v2/directions/driving-car/geojson",
    );

  const body = {
    coordinates: [
      [start[1], start[0]], // [lng, lat]
      [end[1], end[0]],
    ],
  };
  const res = await fetch(url, {
    method: "POST",
    headers: {
      Authorization: ORS_API_KEY,
      "Content-Type": "application/json",
    },
    body: JSON.stringify(body),
  });
  if (!res.ok) return [start, end];
  const data = await res.json();
  if (
    data &&
    data.features &&
    data.features[0] &&
    data.features[0].geometry &&
    data.features[0].geometry.coordinates
  ) {
    // ORS returns [lng, lat], convert to [lat, lng]
    return data.features[0].geometry.coordinates.map(
      ([lng, lat]: [number, number]) => [lat, lng],
    );
  }
  return [start, end];
}

function App() {
  const [pins, setPins] = useState<Pin[]>(initialPins);
  const [startIdx, setStartIdx] = useState<number | null>(null);
  const [endIdx, setEndIdx] = useState<number | null>(null);

  // Handle map click to add a new pin
  const handleMapClick = (e: any) => {
    if (!e.latlng) return;
    const coords: [number, number] = [e.latlng.lat, e.latlng.lng];
    const placeName = prompt("Enter the name of this place:");
    if (!placeName) return;
    setPins((prev) => [...prev, { coords, placeName, media: [], travels: [] }]);
  };

  // Handle media upload for a pin
  const handleMediaUpload = (pinIdx: number, files: FileList) => {
    const fileObjs = Array.from(files).map((file) => ({
      url: URL.createObjectURL(file),
      type: file.type,
      name: file.name,
    }));
    setPins((prev) =>
      prev.map((pin, idx) =>
        idx === pinIdx ? { ...pin, media: [...pin.media, ...fileObjs] } : pin,
      ),
    );
  };

  // Set a pin as start point
  const handleSetStart = (idx: number) => {
    setStartIdx(idx);
    // If end is same as start, reset end
    if (endIdx === idx) setEndIdx(null);
  };

  // Set a pin as end point
  const handleSetEnd = async (idx: number) => {
    setEndIdx(idx);
    // If start is same as end, reset start
    if (startIdx === idx) setStartIdx(null);

    // If a start point is selected, add this end point to the travels of the start pin
    if (startIdx !== null && startIdx !== idx) {
      // Fetch the route from OpenRouteService
      const route = await fetchORSRoute(
        pins[startIdx].coords,
        pins[idx].coords,
      );

      setPins((prevPins) =>
        prevPins.map((pin, i) =>
          i === startIdx
            ? {
                ...pin,
                travels: pin.travels.some((t) => t.to === idx)
                  ? pin.travels
                  : [...pin.travels, { to: idx, route }],
              }
            : pin,
        ),
      );
    }
  };

  return (
    <div className="h-screen w-screen bg-gray-100">
      <MapView
        pins={pins}
        onMapClick={handleMapClick}
        onMediaUpload={handleMediaUpload}
        startIdx={startIdx}
        endIdx={endIdx}
        onSetStart={handleSetStart}
        onSetEnd={handleSetEnd}
      />
    </div>
  );
}

export default App;
